  //Program to print sum,multiply,subtract and divide an remainder of two numbers.
public class Arithmatic {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     int X=10;
     int Y = 3;
     int Sum = X+Y;
     int Diffrence = X-Y;
     int Divide = X/Y;
     int Multiply = X*Y;
     int Remainder = X%Y;
     
     System.out.println("Sum of two numbers are:" +Sum);
     System.out.println("Difference of two numbers are:" +Diffrence);
     System.out.println("Division of two numbers are: " +Divide);
     System.out.println("Multiplication of two numbers are: " +Multiply);
     System.out.println( "Remainder of two numbers are: " +Remainder);
     
      
     
	}

}
