//if-else statement
public class IfElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     int number=18;
     // check if the number is divisible by 2 or not
     if(number%2 == 0) {
    	 System.out.println("\n The number is an even number");
     }
    	 else
    	 {System.out.println("\n The number is an odd number");
     }
     System.out.println("\n The program is completed");
	}

}
